import Table from "./Table"
removeCharacter = (index) => {
    const {characters} = this.state

    this.setState({
        characters: characters.filter((character,i)=>{
            return i !== index
        }),
    })
}
class App extends Component{
    state = {
        character: [{
            name: 'Charlie',
            job: 'Janitor'
        },
        {
            name: 'Mac',
            job: 'Bouncer'
        },
        {
            name: 'Dee',
            job: 'Aspring actress'
        },
        {
            name: 'Dennis',
            job: 'Bartender'
        },
        ],
    }
    render() {
        const {characters} = this.state
        return (
            <div className="Container">
                <Table characterData={characters}/>
            </div>
        )
    }
  }
